import React, { useState } from "react";
import "./Main.css";
import Modal from "../../modal/Modal";
import MovieList from "../../movie/moveList/MovieList";
import MovieFilter from "../../movie/movieFilter/MovieFilter";

const MOVIES = [
  {
    id: 1,
    title: "Nagatoro: 2nd attack",
    url: "data:image/jpeg;base64,",
    rating: 5,
    genre: "романтика",
  },
  {
    id: 2,
    title: "Orient",
    url: "data:image/jpeg;base64,",
    rating: 5,
    genre: "боевики",
  },
];

const Main = ({ modal, modalOpenAndCloseHandler }) => {
  const [movies, setMovies] = useState(MOVIES);
  const [filtered, setFiltered] = useState([]);

  return (
    <main className="main">
      {modal && (
        <Modal
          movies={movies}
          closeModalHandler={modalOpenAndCloseHandler}
          setMovies={setMovies}
        />
      )}
      <MovieFilter
        setMovies={setMovies}
        movies={movies}
        filtered={filtered}
        setFiltered={setFiltered}
      />
      <MovieList setMovies={setMovies} movies={filtered} />
    </main>
  );
};

export default Main;
